    "url": "http://localhost:4000/csp_apps_sam_a7g5/apps/rstc/rstc_reset_cause/readme.html#downloading-and-building-the-application",
    "relUrl": "/apps/rstc/rstc_reset_cause/readme.html#downloading-and-building-the-application"
